mapper.select:
SELECT COUNT(*) CNT
  FROM COMTNMENUINFO
 WHERE MENU_NO = ${MENU_NO}

mapper.insert:
INSERT INTO COMTNMENUINFO (
	  MENU_NO
	, MENU_NM
	, PROGRM_FILE_NM
	, UPPER_MENU_NO
	, MENU_ORDR
	, MENU_DC
	, RELATE_IMAGE_PATH
	, RELATE_IMAGE_NM
) VALUES (
	 #{MENU_NO}
	, #{MENU_NM}
	, #{PROGRM_FILE_NM}
	, #{UPPER_MENU_NO}
	, #{MENU_ORDR}
	, #{MENU_DC}
	, #{RELATE_IMAGE_PATH}
	, #{RELATE_IMAGE_NM}
)

mapper.update:
UPDATE COMTNMENUINFO
   SET MENU_NO = ${MENU_NO}
     	, MENU_NM	= #{MENU_NM} 	
     	, PROGRM_FILE_NM	= #{PROGRM_FILE_NM} 	
     	, UPPER_MENU_NO	= #{UPPER_MENU_NO} 	
     	, MENU_ORDR	= #{MENU_ORDR} 	
     	, MENU_DC	= #{MENU_DC} 	
     	, RELATE_IMAGE_PATH	= #{RELATE_IMAGE_PATH} 	
     	, RELATE_IMAGE_NM	= #{RELATE_IMAGE_NM} 	
 WHERE MENU_NO = ${MENU_NO}

mapper.insert:
INSERT INTO COMTNMENUCREATDTLS (
	  MENU_NO
	, AUTHOR_GRP_CODE
) VALUES (
	 #{MENUNO}
	, #{AUTHGRPCD}
)
